function cost_for_contour  = get_cost(X,target,thetas)  for x = X    for y = target      predicted = x*thetas;      squared_error = (predicted-target).^2;  %disp("SQ ERR");  %disp(squared_error);      cost = (1/(2*size(x,1)))*sum(squared_error);  %cost = squared_error;    endfor  endfor  predicted = X*thetas;  squared_error = (predicted-target).^2;  %disp("SQ ERR");  %disp(squared_error);  cost = (1/(2*size(X,1)))*sum(squared_error);  %cost = squared_error;end